styles:
.clearfix::after {
content: "";
clear: both;
display: table;
}
.chatbot-container{
display: flex;
flex-direction: column;
height: 100%;
}
ion-content{
background-color: #f3f3f3;
}
.chatbot-direct-chat-messages {
overflow: auto;
padding: 10px;
flex-grow: 1;
}
.chatbot-footer{
width: 100%;
}
.bubble-container{
display: block;
padding: 0;
}
.white-bubble, .blue-bubble {
background-color: #fff;
font-size: 15px;
border-top-left-radius: 0em;
border-top-right-radius: 0.5em;
border-bottom-left-radius: 0.5em;
border-bottom-right-radius: 0.5em;
box-shadow: 0 0.4px 0.2px rgba(0,0,0,0.35);
-webkit-box-shadow: 0 0.4px 0.2px rgba(0,0,0,0.35);
width: auto;
display: inline-block;
float: left;
padding-left: 0px;
margin: 0px 0px 4px 0px;
}
.blue-bubble {
background-color: #0084ff;
border-top-left-radius: 0.5em;
border-top-right-radius: 0em;
float: right;
color: #fff;
margin: 0px 0px 5px 0px;
}
.bubble-content {
padding: 10px;
display: flex;
flex-grow: 1;
}
.blue-bubble ul{
margin: 0; padding: 0;list-style: none;
}
.mini_label{
font-size: 12px;margin-bottom: 3px;
}
.btn-inapp {
margin: 0 !important;
color: #656565;
}
.form-content {
padding: 10px;
display: flex;
flex-grow: 1;
}
.form-content ion-label {
color: #000 !important;
font-size: 16px !important;
}
.nonfix {
text-align: center;
margin: 0 auto;
}
.chatbot-action-container{
text-align: center;
padding: 0 10px;
position:relative;
}
.__exit-buttonsInline {
list-style-type: none;
padding-left: 0px;
margin: 10px 0;
}
.__exit-buttonsInline li {
display: inline-flex;
margin: 0 3px 3px;
}
.__exit-buttonsInline li a {
text-decoration: none;
background-color: #ff4d72;
color: #fff;
padding: 8px 15px;
font-size: 15px;
font-weight: bold;
display: block;
width: auto;
border-top-left-radius: 0.2em;
border-top-right-radius: 1.3em;
border-bottom-left-radius: 1.3em;
border-bottom-right-radius: 1.3em;
box-shadow: 0 0.4px 0.2px rgba(0,0,0,0.25);
}
div#wave {
position: relative;
margin-top: 0vh;
text-align: center;
width: 50px;
/* height: 50px; */
margin-left: auto;
margin-right: auto;
}
div#wave .dot {
display: inline-block;
width: 5px;
height: 5px;
border-radius: 50%;
margin-right: 3px;
background: #303131;
animation: wave 1.3s linear infinite;
}
div#wave .dot:nth-child(2) {
animation-delay: -1.1s;
}
div#wave .dot:nth-child(3) {
  animation-delay: -0.9s;
}
@keyframes wave {
	0%, 60%, 100% {
		transform: initial;
	}
	30% {
		transform: translateY(-10px);
	}
}
